import React from 'react';
import { connect } from 'react-redux';
import { searchRestaurants } from '../actions/index';
import { bindActionCreators } from 'redux';

class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            city: ''
        }
    }
    // state = { city: '' };

    onCityInputChange = e => {
        this.setState({
            city: e.target.value
        })

    };

    onFormSubmit = e => {
        e.preventDefault();
        debugger
        this.props.searchRestaurants(this.state.city);
    };

    render() {
        return(
            <div className='city-input'>
                <form onSubmit={this.onFormSubmit}>
                    <label htmlFor="city">City: </label>
                    <input 
                        type="text"
                        name="city"
                        id="city-input"
                        placeholder="Enter a city"
                        onChange={this.onCityInputChange}
                        value={this.state.city} 
                    />
                </form>
            </div>
        )
    }
}

function mapStateToProps(state) {
    // whatever is retured will show up as props inside of BookList
    return {
        restaurants: state.restaurants
    };
}

// anything returned from this function will end up as props
// on the BookList container
function mapDispatchToProps(dispatch) {
    // whenever selectBook is called, the result should be passed
    // to all of our reducers
    return bindActionCreators({ searchRestaurants: searchRestaurants }, dispatch);
}

// promote BookList from a component to a container - it needs to know
// about this new dispatch methos, selectBook. Make it available
// as a prop
export default connect(mapStateToProps, { searchRestaurants })(SearchBar);
// export default connect(mapStateToProps, mapDispatchToProps)(SearchBar);
