import openTable from '../apis/openTable';

export const searchRestaurants = async(city = '') => {
    const restaurant = await openTable.get(`/restaurants?city=${city}`);
    return {
        type: 'SEARCH_RESTAURANTS',
        payload: restaurant
    }
}
