import openTable from '../apis/openTable';

export const searchRestaurants = (city = '') => {
    return async (dispatch) => {
        const response = await openTable.get(`/restaurants?city=${city}`);

        dispatch({
            type: 'SEARCH_RESTAURANTS',
            payload: response.restaurants
        })
    }
}
