import React from 'react';
// import RestaurantItem from './restaurant-item';

class RestaurantContainer extends React.Component {
    constructor(props) {
        super(props);
        // this.handleFilterCity = this.handleFilterCity.bind(this);
        this.state = {
            restaurants: [],
            city: 'toronto'
        }
    }

    componentDidMount() {
        fetch(`https://opentable.herokuapp.com/api/restaurants?city=${this.state.city}`)
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({restaurants: result.restaurants});
            }
        )
    }

    render() {
        const restaurantsList = [];

        this.state.restaurants.forEach(restaurant => {
            restaurantsList.push(
                <RestaurantsItem key={restaurant.id} restaurant={restaurantsList}/>
                // <li>{restaurant.name}</li>
            )
        })

        return (
            <div className="">
                <ul>{restaurantsList}</ul>
            </div>
        )
    }
}

export default RestaurantContainer;
