export function searchRestaurants(restaurant) {
    return dispatch => {
        fetch(`https://opentable.herokuapp.com/api/restaurants?city=${city}`)
        .then(res => res.json())
        .then(
            (result) => {
                dispatch(restaurants(result.restaurants))
            }
        )
    }
}

export const restaurants = (restaurant) => {
        return {
            type: 'SEARCH_RESTAURANTS',
            payload: restaurant
    }
}
