import React from 'react';
import RestaurantItem from './RestaurantItem';
import SearchBar from './SearchBar';
import openTable from '../apis/openTable';

class RestaurantContainer extends React.Component {
    constructor(props) {
        super(props);
        this.handleOnChangeCity = this.handleOnChangeCity.bind(this);
        this.state = {
            restaurants: [],
            city: 'toronto'
        }
    }

    componentDidMount() {
        this.fetchRestaurants(this.state.city);
    }

    handleOnChangeCity(e) {
        this.setState({
            city: e.target.value
        })
    }

    onFormSubmit = city => {
        this.fetchRestaurants(city);
    }

    fetchRestaurants = city => {
        fetch(`https://opentable.herokuapp.com/api/restaurants?city=${this.state.city}`)
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({ restaurants: result.restaurants });
            }
        )
    }

    render() {
        const restaurantsList = [];

        this.state.restaurants.forEach(restaurant => {
            restaurantsList.push(
                <RestaurantItem key={restaurant.id} restaurant={restaurant}/>
            )
        })

        return (
            <div className="restaurant-container">
                <SearchBar 
                    onFormSubmit={this.onFormSubmit} />
                <ul>{restaurantsList}</ul>
            </div>
        )
    }
}

export default RestaurantContainer;
